import React, {useState, useRef, useEffect} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from './AuthContext'

import './SeePost_css.css'

const SeePost = () => {

    const headingRef = useRef()
    const imageRef = useRef()
    const descriptionRef = useRef()
    const commentsRef = useRef()

    const [heading, setHeading] = useState('')
    const [image, setImage] = useState('')
    const [description, setDescription] = useState('')
    const [userId, setUserId] = useState('')
    const [vehicles, setVehicles] = useState([])
    const [comments, setComments] = useState('')

    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [loading, setLoading] = useState(false)

    const {currentUser} = useAuth()
    const navigate= useNavigate()

    const [currentPostIndex, setCurrentIndex] = useState(0)
    const postContainerRef = useRef()
    const postHeight = 100
    const scrollInterval = 5000

    const[posts, setPosts] = useState([
        {id:1, title: 'Porsche 911 GB', user:'Sharusan', profilePicture:'http://localhost:3001/uploads/3f736944d3729d94506db63f9b3f6335', image:`http://localhost:3001/uploads/b9453f083d3aaf3f3826875e2742faad`, description: 'Description 1', date:'26/01/24', time:'18:00', vrn:'91A 8GB'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
    ])

    useEffect(() => {
        const nextPost = () => {
            setCurrentIndex((prevIndex) =>
                prevIndex < posts.length-1 ? prevIndex +1 : 0
            )
        }

        const intervalId = setInterval(nextPost, scrollInterval)

        return () => clearInterval(intervalId)
    }, [posts])

    useEffect(() => {
        if(postContainerRef.current){
            postContainerRef.current.scrollTop = currentPostIndex * postHeight
        }
    }, [currentPostIndex])


    async function handleAddPost(){

    }

    return (
        <>
            <div className="postList" ref={postContainerRef}>
                {posts.map((post, index) => (
                    <div key={post.id} className={`post ${index === currentPostIndex ? 'active' : ''}`}>
                        <div className='postHead'>
                            <div className='postUserDetails'>
                                {post.profilePicture && (
                                    <img className='postUserImage'
                                        src={post.profilePicture} 
                                        alt="Profile"
                                    />
                                )}                                
                                <p className='postUserName'>{post.user}</p>
                            </div>
                            <div className='postTimeStamp'>
                                <p>{post.date}</p>
                                <p>{post.time}</p>
                            </div>
                        </div>

                        <div className='post-content'>
                            <h2 className='postTitle'>{post.title}</h2> 
                            <img src={post.image} alt={post.title} className='postImage'/>
                            <div className='postFooter'>
                                <p className='postDescription'>{post.description}</p> 
                                <p className='postVRN'>{post.vrn}</p>
                            </div> 
                        </div> 
                    </div>
                ))}
                    <p>{error}</p>
            </div>    

        </>
    )
}

export default SeePost
