import React, {useContext, useEffect, useState} from 'react'

const AuthContext = React.createContext()

export function useAuth() {
    return useContext(AuthContext)
}

export function AuthProvider ({children}) {
    const [currentUser, setCurrentUser] = useState()
    const [loading, setLoading] = useState(true)

    function signup(email, password){
      return auth.createUserWithEmailAndPassword(email, password)
    }

    async function fetchUserData(){
      try{
          const firebaseUID = currentUser.uid;

          const response = await fetch(`http://localhost:3001/users?userfb=${encodeURIComponent(firebaseUID)}`, {
              method: 'GET',
              headers: {
                  'accept': 'application/json',
              },
          });

          if (response.ok){
              const data = await response.json()

              setuserId(data.userData._id)
              setuserName(data.userData.username)
              setprofilePicture(data.userData.profilePicture)
              setVehicle(data.userData.vehicles)

              console.log("Fetched User Details")
              return data
          } else{
              const errorData = await response.json()
              throw new Error(errorData.message)
          }
      }catch (error){
          console.error("Error Fetching User Data:", error)
      }
  }

    function login(email, password){
      return auth.signInWithEmailAndPassword(email, password)
    }

    function logout(){
      return auth.signOut()
    }

    function passwordReset(email){
      return auth.sendPasswordResetEmail(email)
    }

    function updateEmail(email){
      return currentUser.verifyBeforeUpdateEmail(email)
    }

    function updatePassword(password){
      return currentUser.updatePassword(password)
    }

    function deleteUser(){
      return currentUser.delete()
    }

    useEffect(() => {
      const unsubscribe = auth.onAuthStateChanged(user =>{
        setCurrentUser(user)
        setLoading(false)
      })

      return unsubscribe
    }, [])



    const value  = {
        currentUser,
        signup,
        login,
        logout,
        passwordReset,
        updateEmail,
        updatePassword,
        deleteUser
    }


  return (
    <AuthContext.Provider value = {value}>
      {!loading && children}
    </AuthContext.Provider>
  )
}
