import React, {useRef, useState} from 'react'
import {Form} from 'react-bootstrap'
import {Link, useNavigate} from 'react-router-dom'

import './RegisterCar_css.css'


const RegisterCar = () => {

    const vehicleReg = useRef()
    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [showButton, setShowButton] = useState(false)
    const navigate = useNavigate()



    async function handleSubmit(e){
        e.preventDefault()
        setShowButton(false)


        try{
            setError('');
            setMessage('')

            const username = 'MeetMyCar5';

            const apiVRNResponse = await fetch(
            `https://www.regcheck.org.uk/api/reg.asmx/Check?RegistrationNumber=${vehicleReg.current.value}&username=${username}`
            );

            if (apiVRNResponse.ok) {
                const data = await apiVRNResponse.text();
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(data, 'text/xml');

                const vehicleMake = xmlDoc.querySelector('MakeDescription').textContent;
                const vehicleModel = xmlDoc.querySelector('ModelDescription').textContent;

                const vehicleFuel = xmlDoc.querySelector('FuelType').textContent;
                const vehicleTransmission = xmlDoc.querySelector('Transmission').textContent;

                setMessage(
                    `Make: ${vehicleMake || 'No Make available.'}` +
                    `Model: ${vehicleModel || 'No Model available.'}` +
                    `Fuel Type: ${vehicleFuel || 'No Fuel available.'}` +
                    `Transmission: ${vehicleTransmission || 'No Transmission available.'}`
                );


            } else {
                setError("Failed To Find Vehicle");
                setMessage(
                    `Make: Mercedes \n` +
                    `Model: CLA \n` +
                    `Fuel Type: Petrol \n` +
                    `Transmission: Automatic \n`
                );
                setShowButton(true);

            }
        } catch (error) {
            setError('Failed To Retrieve Vehicle Information');
            console.log(error)

        }
    }



  return (
      <>
            <div className="Card">
                <header>
                    <h1 id="addVehicle_text">
                        Add Your Vehicle
                        <h2 id="slogan_text">Join Our Family</h2>
                    </h1>
                    <div id="red-corner"></div>
                </header>

                <form onSubmit={handleSubmit} className='addVehicle_Form'>
                    <div className='vrn_row'>
                        <Form.Group id="vrn">
                            <Form.Control id="vrn_input" type="text" placeholder='Registration Number' ref={vehicleReg} required />
                        </Form.Group>
                    </div>

                    <p className="w-100 text-center mt-3 mb-1" id="error_Msg">{error}</p>

                    <button id="button" className="w-100 mt-2" type="submit">Search Car</button>

                    <p id='vehicleDetail'>{message}</p>

                    {showButton && (
                        <button id="button" className="w-100 mt-2" type="submit">Add Car</button>
                    )}
                </form>


                <div id="findVehicle" className="w-100 text-center mt-3">
                    Can't find you car? <Link to="/login" id='findlink'> Find Here</Link>
                </div>
            </div>

      </>

  )
}

export default RegisterCar
