import React, {useState, useRef} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from './AuthContext'

import './SeePost_css.css'

const SeePost = () => {

    const headingRef = useRef()
    const imageRef = useRef()
    const descriptionRef = useRef()
    const commentsRef = useRef()

    const [heading, setTitle] = useState('')
    const [image, setImage] = useState('')
    const [description, setDescription] = useState('')
    const [userId, setUserId] = useState('')
    const [vehicles, setVehicles] = useState([])
    const [comments, setComments] = useState('')

    const [message, setMessage] = useState('')
    const [loading, setLoading] = useState(false)

    const {currentUser} = useAuth()
    const navigate= useNavigate()

    const[posts, setPosts] = useState([
        {id:1, title: 'Porsche 911 GB', user:'Sharusan', profilePicture:'http://localhost:3001/uploads/3f736944d3729d94506db63f9b3f6335', image:`http://localhost:3001/uploads/b9453f083d3aaf3f3826875e2742faad`, description: 'Check My Car Fastest Car In The World', date:'26/01/24', time:'18:00', vrn:'EA64 SYJ', speedtime:'2.5', bhp:'300', torque:'100'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
        {id:2, title: 'Post 2', user:'Mate', image:'/uploads', description: 'Description 2'},
    ])

    useEffect(() => {
        async function fetchUserData(){
            await updateUser()
            try{
                setError('')
                const firebaseUID = currentUser.uid;
                // console.log(firebaseUID)

                const response = await fetch(`http://localhost:3001/users/details?userfb=${encodeURIComponent(firebaseUID)}`, {
                    method: 'GET',
                    headers: {
                        'accept': 'application/json',
                    },
                });

                if (response.ok){
                    const data = await response.json()

                    setUserId(data.userData._id)
                    setfirstName(data.userData.firstname)
                    setlastName(data.userData.lastname)
                    setuserName(data.userData.username)
                    setprofilePicture(data.userData.profilePicture)
                    setVehicles(data.userData.vehicles)

                    // console.log(`User: FirstName ${firstname} LastName: ${lastname}  UserName: ${username}
                    //              Profile Picture: ${profilePicture} Vehicles: ${vehicles}`)   

                    console.log("Fetched User Details")
                    return data
                } else{
                    const errorData = await response.json()
                    setError("Failed To Fetch User Data")
                    throw new Error(errorData.message)
                }
            }catch (error){
                console.error("Error Fetching User Data:", error)
                setError("Failed To Fetch User Data")
            }
        }
        fetchUserData();
        // eslint-disable-next-line
    }, [currentUser.uid]);


    return (
        <>
            <div className="postList">
                {posts.map((post) => (
                    <div key={post.id} className='post'>
                        <div className='postHead'>
                            <div className='postUserDetails'>
                                {post.profilePicture && (
                                    <img className='postUserImage'
                                        src={post.profilePicture} 
                                        alt="Profile"
                                    />
                                )}                                
                                <p className='postUserName'>{post.user}</p>
                            </div>
                            <div className='postTimeStamp'>
                                <p>{post.date}</p>
                                <p>{post.time}</p>
                            </div>
                        </div>

                        <div className='post-content'>
                            <h2 className='postTitle'>{post.title}</h2> 
                            <img src={post.image} alt={post.title} className='postImage'/>

                            <div className='postSpecs'>
                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,red 0% ${((1.5/post.speedtime)*100)}%,white ${0}% 100%)`}}>
                                    <h3>{post.speedtime} 
                                        <span>secs</span>
                                    </h3>
                                    <p className='TopSpecsName'> 0 - 60</p>
                                </div>

                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,orange 0% ${((post.bhp/1000)*100)}%,white ${0}% 100%)`}}>
                                    <h3>{post.bhp}
                                        <span>bhp</span>
                                    </h3>
                                    <p className='TopSpecsName'>BHP</p>
                                </div>

                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,green 0% ${post.torque}%,white ${100-post.torque}% 100%)`}}>
                                    <h3>{post.torque}
                                        <span>Nm</span>
                                    </h3>
                                    <p className='TopSpecsName'>Torque</p>
                                </div>

                            </div>

                            <div className='postFooter'>
                                <Link className='postVRN'>{post.vrn}</Link>
                                <p className='postDescription'>{post.description}</p> 
                            </div> 
                        </div> 
                    </div>
                ))}
            </div>    
        </>
    )
}

export default SeePost
