    }

    return (
        <>

            <div className="Card">
                <header>
                    <h1 id="addVehicle_text">
                        Check Below
                        <p id="slogan_text">See What Your Friends Are Doing</p>
                    </h1>
                    <div id="red-corner"></div>
                </header>

                <form onSubmit={handleAddPost} className='addPost_Form'>
                    <div>
                        <Form.Group id="">
                            <Form.Control id="" type="text" placeholder='Heading' ref={headingRef} required />
                        </Form.Group>

                        <Form.Group id="">
                            <Form.Control id="" type="text" placeholder='Image' ref={headingRef} required />
                        </Form.Group>

                        <Form.Group id="">
                            <Form.Control id="" type="text" placeholder='Description' ref={headingRef} required />
                        </Form.Group>
                    </div>

                    <p className="w-100 text-center mt-3 mb-1" id="error_Msg">{error}</p>

                    <button id="button" className="w-100 mt-2" type="submit">Search Vehicle</button>

                    <p id='vehicleDetail'>{message}</p>
                </form>

                <AutoPlaySwipeableViews index={index} onChangeIndex={handleNextPost}>
                    {posts.map((post, index) => (
                        <div key={post.id}>
                            <h3>{post.user}</h3>
                            <h2>{post.heading}</h2>
                            <img src={post.images} alt={post.heading} style={{maxWidth: '100%', maxHeight:300}} />
                            <p>{post.description}</p>
                        </div>
                    ))}
                </AutoPlaySwipeableViews>

                <div id="findVehicle" className="w-100 text-center mt-2">
                    Can't find your car? <Link id='findlink' > Find Here</Link>
                </div>
            </div>

        </>
    )
}

export default SeePost
