import React, {useRef, useState} from 'react'
import {Form} from 'react-bootstrap'
import { useAuth } from './AuthContext'
import {Link, useNavigate} from 'react-router-dom'

import './Login_css.css'


const Reauthenticate = () => {

    const passwordRef = useRef()
    const {currentUser, reauthenticateUser} = useAuth()
    const [error, setError] = useState('')
    const navigate = useNavigate()


    const handleReauthenticate = async () => {
        setError('')

        try{
            await reauthenticateUser(passwordRef)
            navigate("/update-profile")
        }catch(error){
            setError('Reauthentication Failed. Please check your password.');
        }
    }


  return (
      <>
            <div className="Card">

            <header>
                    <h1 id="login_text">
                        Reauthenticate
                    </h1>
                    <div id="red-corner"></div>
                </header>

                <form onSubmit={handleSubmit} className='loginForm'>

                    <Form.Group id="password">
                        <Form.Control type="password" placeholder="Password" ref={passwordRef} required />
                    </Form.Group>

                    <p className="w-100 text-center mt-2 mb-0" id="error_Msg">{error}</p>

                    <button disabled={loading} id="button" className="w-100 mt-3" >Reauthenticate</button>

                    <div id="forgotPassPage" className="w-100 text-center mt-3">
                        <Link to="/forgot-password" id="forgotLink">Forgot Password</Link>
                    </div>
                </form>
            </div>
      </>

  )
}

export default Login
