import React, {useRef, useState} from 'react'
import {Form} from 'react-bootstrap'
import {Link} from 'react-router-dom'

import './RegisterCar_css.css'


const RegisterCar = () => {

    const carOptions = {
        Mercedes: ["CLA", "C Class", "E Class"],
        Toyota: ["Corolla", "Camry", "RAV4"],
        Honda: ["Civic", "Accord", "CR-V"],
    };

    const vehicleReg = useRef()
    const [vehicleMake, setvehicleMake] = useState('')
    const [vehicleModel, setvehicleModel] = useState([])
    const [vehicleFuel, setvehicleFuel] = useState('')
    const [vehicleTransmission, setvehicleTransmission] = useState('')


    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [showButton, setShowButton] = useState(false)
    const [showOptions, setShowOptions] = useState(false)


    const handleSelectCar= (event) => {
        const selectMake = event.target.value;
        setvehicleMake(selectMake)
        setvehicleModel(selectMake ? carOptions[selectMake] : []);
    };

    const handleFindCar= () => {
        setMessage('')
        setError('')
        setShowOptions(true)
        setShowButton(true)
    };

    async function handleSubmit(e){
        e.preventDefault()
        setShowButton(false)

        try{
            setError('');
            setMessage('')

            const username = 'MeetMyCar7';

            const apiVRNResponse = await fetch(
            `https://www.regcheck.org.uk/api/reg.asmx/Check?RegistrationNumber=${vehicleReg.current.value}&username=${username}`
            );

            if (apiVRNResponse.ok) {
                const data = await apiVRNResponse.text();
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(data, 'text/xml');

                VRN_vehicleMake = xmlDoc.querySelector('MakeDescription').textContent;
                setvehicleMake(VRN_vehicleMake)
                VRN_vehicleModel = xmlDoc.querySelector('ModelDescription').textContent;
                setvehicleModel(VRN_vehicleModel)
                vehicleFuel = xmlDoc.querySelector('FuelType').textContent;
                s
                vehicleTransmission = xmlDoc.querySelector('Transmission').textContent;

                setMessage(
                    `Make: ${vehicleMake || 'No Make available.'} \n` +
                    `Model: ${vehicleModel || 'No Model available.'} \n` +
                    `Fuel Type: ${vehicleFuel || 'No Fuel available.'} \n` +
                    `Transmission: ${vehicleTransmission || 'No Transmission available.'} \n`
                );

                setShowOptions(false)


            } else {
                setError("Failed To Find Vehicle");
                setMessage(false);
                setShowButton(true);
                setShowOptions(true)
            }

        } catch (error) {
            setError('Failed To Retrieve Vehicle Information');
            console.log(error)
        }
    }





  return (
      <>
            <div className="Card">
                <header>
                    <h1 id="addVehicle_text">
                        Add Your Vehicle
                        <p id="slogan_text">Show Your Vehicle</p>
                    </h1>
                    <div id="red-corner"></div>
                </header>

                <form onSubmit={handleSubmit} className='addVehicle_Form'>
                    <div className='vrn_row'>
                        <Form.Group id="vrn">
                            <Form.Control id="vrn_input" type="text" placeholder='Registration Number' ref={vehicleReg} required />
                        </Form.Group>
                    </div>

                    <p className="w-100 text-center mt-3 mb-1" id="error_Msg">{error}</p>

                    <button id="button" className="w-100 mt-2" type="submit">Search Car</button>

                    <p id='vehicleDetail'>{message}</p>

                    {showOptions && (<div>
                            <select onChange={handleSelectCar} value={vehicleMake}>
                                <option value="">Make</option>
                                {Object.keys(carOptions).map(make => (
                                    <option key={make} value={make}> {make} </option>
                                ))}
                                {}
                            </select>

                            <select disabled={!vehicleMake}>
                                <option value="">Model</option>
                                {vehicleModel.map(model => (
                                    <option key={model} value={model}> {model} </option>
                                ))}
                                {}
                            </select>
                        </div>
                    )}

                    {showButton && (
                        <button id="button" className="w-100 mt-2" type="submit">Add Car</button>
                    )}
                </form>


                <div id="findVehicle" className="w-100 text-center mt-2">
                    Can't find your car? <Link id='findlink' onClick={handleFindCar}> Find Here</Link>
                </div>
            </div>

      </>

  )
}

export default RegisterCar
