import React, {useState, useEffect, useRef} from 'react'
import { Link, useNavigate } from 'react-router-dom'

import './SeePost_css.css'

const AddPost = () => {

    const titleRef = useRef()
    const imageRef = useRef()
    const descRef  = useRef()

    const navigate = useNavigate()
    const[posts, setPosts] = useState([])

    useEffect(() => {
        async function fetchPostData(){
            try{
                const response = await fetch(`http://localhost:3001/posts/view`, {
                    method: 'GET',
                    headers: {
                        'accept': 'application/json',
                    },
                });

                if (response.ok){
                    const data = await response.json()

                    setPosts(data.postData)

                    console.log("Fetched Post Details")
                    return data
                } else{
                    const errorData = await response.json()
                    throw new Error(errorData.message)
                }
            }catch (error){
                console.error("Error Fetching Post Data:", error)
            }
        }
        fetchPostData();
    }, []);


    return (
        <>
            <div className='post'>
                <h2>Add Post</h2>

                <div className='postHead'>
                    <div className='postUserDetails'>
                        {post.user.profilePicture && (
                            <img className='postUserImage'
                                src={`http://localhost:3001/${post.user.profilePicture}`} 
                                alt="Profile"
                            />
                        )}                                
                        <p className='postUserName'>{post.user.username}</p>
                    </div>
                    <div className='postTimeStamp'>
                        <p>{formatDate(post.updatedAt)}</p>
                        <p>{formatTime(post.updatedAt)}</p>
                    </div>
                </div>

                <div className='post-content'>

                            <input type='text'ref={titleRef} placeholder='Title...' className='postTitle'></input> 

                            <input type='file'ref={imageRef} placeholder='Insert Image' className='postImage'></input> 

                            <div className='postFooter'>
                                <select className='postVRN'>
                                    <option>Car 1</option>
                                </select>
                                <input type='text' ref={descRef} className='postDescription'></input> 
                            </div>
                </div> 
            </div>
        </>
    )
}

export default AddPost
