import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { useEffect, useState } from 'react';

const useFirebase = () => {

    const [fbKey, setFBKey] = useState('')


    useEffect(() => {
        async function fetchFirebaseConfig() {
            try {
            const response = await fetch('http://localhost:3001/firebaseconfig');
            if (!response.ok) {
                throw new Error('Failed to fetch Firebase config');
            }
            const data = await response.json(); 
            setFBKey(data)

            } catch (error) {
            console.error('Error fetching Firebase config:', error);
            return null;
            }
        }

        fetchFirebaseConfig()
    }, [])


    const app = firebase.initializeApp({
        apiKey: fbKey.apiKey,
        authDomain: fbKey.authDomain,
        projectId: fbKey.projectId,
        storageBucket: fbKey.storageBucket,
        messagingSenderId: fbKey.messagingSenderId,
        appId: fbKey.appId
    })

    const auth = firebase.in.auth()
}

export default useFirebase
