import { useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

const [fbKey, setFBKey] = useState('')

async function fetchFirebaseConfig(){
    try{
        const response = await fetch('http://localhost:3001/firebaseConfig')

        if (!response.ok){
            const errorData = await response.json()
            throw new Error(errorData.message)
        }

        const data = await response.json()
        setFBKey(data)

    }catch (error){
        console.error("Error Initalising Firebase:", error)
        throw error
    }
}

const app = firebase.initializeApp({
    apiKey: fbKey.apiKey,
    authDomain: fbKey.authDomain,
    projectId: fbKey.authDomain,
    storageBucket: "meetmycar-bfa6b.appspot.com",
    messagingSenderId: "671944782564",
    appId: "1:671944782564:web:45127b75a322edce15f2f1"
})

export const auth = app.auth()
export default app
