    }

    return (
        <>

            <div className="Card">
                <header>
                    <div id="red-corner"></div>
                </header>

                <AutoPlaySwipeableViews index={index} onChangeIndex={handleNextPost} interval={20000} enableMouseEvents className='post'>
                    {posts.map((post, index) => (
                        <div key={post.id} className='post' style={{backgroundImage: `url(${post.image})` }}>
                            <img src={post.image} alt={post.heading}/>
                            <div className='post-content'>
                                <h3>{post.user}</h3>
                                <h2>{post.heading}</h2> 
                                <p>{post.description}</p> 
                                <nav>
                                    <p>Like|Comment|Share</p>
                                </nav>
                            </div> 
                        </div>
                    ))}
                </AutoPlaySwipeableViews>

            </div>
        </>
    )
}

export default SeePost
