import React, {useEffect, useState} from 'react'
import { useAuth } from './AuthContext'
import {useNavigate} from 'react-router-dom'

const VerifyEmail = () => {

    const [verified, setVerified] = useState(false)
    const {currentUser} = useAuth()

    const [message, setMessage] = useState('')
    const [error, setError] = useState('')

    const navigate = useNavigate()


    useEffect(() => {
        const checkVerified = async () => {
            await currentUser.reload();
            if (currentUser.emailVerified){
                setVerified(true)
                clearInterval(intervalId)
                navigate("/registervehicle")
            }
        }
        const intervalId = setInterval(checkVerified, 1000)
        return () => clearInterval(intervalId)
    }, [currentUser, navigate]); 

    const resendEmail = async() => {
        setError('')
        setMessage('')
        try{
            await currentUser.sendEmailVerification();
            setMessage("Verification Email Sent")
        }catch (error){
            if (error.code === 'auth/too-many-requests'){
                setError("Verify Current Email Address")
                // sendEmailVerify()
            }else if (error.code === 'auth/requires-recent-login'){
                setError("Login Session Timeout")
                setTimeout(() => {
                    logout()
                }, 2000)
            }else{
                setError("Failed to Update Account")
            }
            setError("Failed To Send Verification Email")
            console.log("Error Sending Verification Email", error)
        }
    }

  return (
      <>
            <body>
                <h1 className="text-center mb-4">Verify Email</h1>
                <div>
                    <p>{message}</p>
                    <p>{error}</p>
                    {verified 
                       ? (
                            <p>Email Verified Reloading....</p>
                        ):(
                        <>
                            <p>Please Verify Email. Check Inbox or Junk</p>
                            <button onClick={resendEmail}>
                                Resend Verification Email
                            </button>
                        </>
                    )}
                </div>
            </body>
      </> 
  )
}

export default VerifyEmail
