import React, {useContext, useEffect, useState} from 'react'
import {auth} from './firebase'

const AuthContext = React.createContext()

export function useAuth() {
    return useContext(AuthContext)
}

export function AuthProvider ({children}) {
    const [currentUser, setCurrentUser] = useState()
    const [loading, setLoading] = useState(true)

    function signup(email, password){
      return auth.createUserWithEmailAndPassword(email, password)
    }

    function login(email, password){
      return auth.signInWithEmailAndPassword(email, password)
    }

    function logout(){
      return auth.signOut()
    }

    function passwordReset(email){
      return auth.sendPasswordResetEmail(email)
    }

    function updateEmail(email){
      return currentUser.verifyBeforeUpdateEmail(email)
    }

    function updatePassword(password){
      return currentUser.updatePassword(password)
    }

    function deleteUser(){
      return currentUser.delete()
    }

    useEffect(() => {
        // Ensure auth is not undefined
        if (!auth) {
            console.log('Firebase Auth is not initialized yet.');
            setLoading(false); // or keep loading true based on your loading strategy
            return;
        }

        const unsubscribe = auth.onAuthStateChanged(user => {
            setCurrentUser(user);
            setLoading(false);
        });

        return unsubscribe;
    }, []);

    // Placeholder functions that check for auth initialization
    const safeAuthOperation = (operation) => (...args) => {
        if (!auth) {
            console.error('Attempting to use Firebase Auth before it is initialized.');
            return Promise.reject('Firebase Auth is not initialized.');
        }
        return operation(...args);
    };



    const value  = {
        currentUser,
        signup,
        login,
        logout,
        passwordReset,
        updateEmail,
        updatePassword,
        deleteUser
    }


  return (
    <AuthContext.Provider value = {value}>
      {!loading && children}
    </AuthContext.Provider>
  )
}
