// firebase.js

import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

let initialized = false;

export async function fetchFirebaseConfig() {
  try {
    const response = await fetch('http://localhost:3001/firebaseconfig');
    if (!response.ok) {
      throw new Error('Failed to fetch Firebase config');
    }
    return await response.json();
  } catch (error) {
    console.error('Error fetching Firebase config:', error);
    return null;
  }
}

export async function initializeFirebase() {
  if (initialized) {
    return; // Firebase already initialized, no need to initialize again
  }

  const firebaseConfig = await fetchFirebaseConfig();

  if (!firebaseConfig) {
    throw new Error('Failed to fetch Firebase config');
  }

  // Initialize Firebase with the fetched config
  firebase.initializeApp(firebaseConfig);
  initialized = true;
}

// Export auth object after Firebase initialization is complete
export const auth = firebase.auth();
