// firebase.js

import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

let initialized = false;

async function fetchFirebaseConfig() {
  try {
    const response = await fetch('http://localhost:3001/firebaseconfig');
    if (!response.ok) {
      throw new Error('Failed to fetch Firebase config');
    }
    const data =  await response.json();
  } catch (error) {
    console.error('Error fetching Firebase config:', error);
    return null;
  }
}

export function initializeFirebase() {
  if (initialized) {
    return Promise.resolve(); // Firebase already initialized, return resolved promise
  }

  return new Promise((resolve, reject) => {
    fetchFirebaseConfig()
      .then(firebaseConfig => {
        if (firebaseConfig) {
          // Initialize Firebase with the fetched config
          firebase.initializeApp(firebaseConfig);
          initialized = true;
          resolve();
        } else {
          reject(new Error('Failed to fetch Firebase config'));
        }
      })
      .catch(error => {
        console.error('Error initializing Firebase:', error);
        reject(error);
      });
  });
}

// Export auth object after Firebase initialization is complete
export const auth = firebase.auth();
