import React, {useRef, useState} from 'react'
import {Form} from 'react-bootstrap'
import {Link, useNavigate} from 'react-router-dom'

import './Signup_css.css'


const RegisterCar = () => {

    const vehicleReg = useRef()
    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate()



    async function handleSubmit(e){
        e.preventDefault()

        try{
            setLoading(true);
            setError('');
            const username = 'MeetMyCar';

            const apiVRNResponse = await fetch(
            `https://www.regcheck.org.uk/api/reg.asmx/Check?RegistrationNumber=${vehicleReg.current.value}&username=${username}`
            );

            if (apiVRNResponse.ok) {
                const xmlResponse = await apiVRNResponse.text();
                const parser = new DOMParser();
                const xmlDoc = parser.parseFromString(xmlResponse, 'text/xml');

                const carMake = xmlDoc.querySelector('MakeDescription').textContent;
                const carModel = xmlDoc.querySelector('ModelDescription').textContent;

                setMessage(carMake + ' ' +  carModel || 'No description available.');
            } else {
                setError("Failed To Find Car");
            }
        } catch (error) {
            setError('Failed To Find Car');
        }
        setLoading(false);
    }



  return (
      <>
            <div className="Card">
                <header>
                    <h1 id="register_text">
                        Add Your Car
                        <h2 id="slogan_text">Join Our Family</h2>
                    </h1>
                    <div id="red-corner"></div>
                </header>

                <form onSubmit={handleSubmit}>
                    <div className='vrn_row'>
                        <Form.Group id="vrn">
                            <Form.Control id="vrn_input" type="text" placeholder='Registration Number' ref={vehicleReg} required />
                        </Form.Group>
                    </div>

                    <p className="w-100 text-center mt-3 mb-1" id="error_Msg">{error}</p>

                    <button disabled={loading} id="button" className="w-100 mt-2" type="submit">Search Car</button>
                    {message}
                </form>

                <img src="https://i.ibb.co/FsSc9k1/Screenshot-2023-11-07-at-18-34-47-fotor-bg-remover-20231107185715.png" alt="Image Of Porsche Car"/>

                <div id="loginpage" className="w-100 text-center mt-2">
                    Can't find you car? <Link to="/login" id='loginlink'> Find Here</Link>
                </div>
            </div>

      </>

  )
}

export default RegisterCar
