import React, {useState, useEffect} from 'react'
import { Button } from 'react-bootstrap'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from './AuthContext'

import './Setting_css.css'

const Setting = () => {
  const [userId, setuserId] = useState('')
  const {currentUser, logout} = useAuth()

  const [error, setError] = useState("")
  const navigate = useNavigate()

  useEffect(() => {
    async function fetchUserData(){
        try{
            setError('')
            const firebaseUID = currentUser.uid;

            const response = await fetch(`http://localhost:3001/users/details?userfb=${encodeURIComponent(firebaseUID)}`, {
                method: 'GET',
                headers: {
                    'accept': 'application/json',
                },
            });

            if (response.ok){
                const data = await response.json()

                setuserId(data.userData._id)
                console.log("Fetched User Details")
                return data
            } else{
                const errorData = await response.json()
                setError("Failed To Fetch User Data")
                throw new Error(errorData.message)
            }
        }catch (error){
            console.error("Error Fetching User Data:", error)
            setError("Failed To Fetch User Data")
        }
    }
    fetchUserData();
}, [currentUser.uid]);

  async function handleLogOut(){
    setError('')

    try{
      await logout()
      navigate('/login')
    }catch{
      setError('Failed to log out')
    }
  }

  return (
    <div>
        <header className='garageHeader'>
            <h1 id="login_text">
                Garage
                <p id="slogan_text">Check Out Your Vehicles</p>
            </h1>

            <div className='showUserData'>
                <div className='showUserDataNum'>
                    <p> <span>{vehicle.length}</span> Vehicles</p>
                    <p onClick={() => setOpenWinModal(true)}> <span>{profileWins.length}</span> Wins</p>
                    <p onClick={() => setOpenLostModal(true)}> <span>{profileLosts.length}</span> Losts</p>
                </div>

                <Link to="/registervehicle" className="btn btn-dark" id='checkbtn'> Add Vehicle</Link>  
            </div>

        </header> 
        <h1 className="text-center mb-4">Setting</h1>
        <p>{error}</p>
        <strong>Email: </strong> {currentUser.email}

        <Link to="/reauthenticate" className="btn btn-primary w-100 mt-3"> Update Profile</Link>

        <Link to={`/garage/${userId}`} className="btn btn-dark w-100 mt-3"> Check Garage</Link>

        <div className="w-100 text-center mt-2">
            <Button variant="link" onClick={handleLogOut}> Log Out </Button>
        </div>

    </div>
  )
}

export default Setting
