import React, {useState, useEffect} from 'react'
import { Button } from 'react-bootstrap'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from './AuthContext'

import './Setting_css.css'

const Setting = () => {
  const [userId, setuserId] = useState('')
  const [username, setuserName] = useState('')
  const [profilePicture, setprofilePicture] = useState('')

  const {currentUser, logout} = useAuth()

  const [error, setError] = useState("")
  const navigate = useNavigate()

  useEffect(() => {
    async function fetchUserData(){
        try{
            setError('')
            const firebaseUID = currentUser.uid;

            const response = await fetch(`http://localhost:3001/users/details?userfb=${encodeURIComponent(firebaseUID)}`, {
                method: 'GET',
                headers: {
                    'accept': 'application/json',
                },
            });

            if (response.ok){
                const data = await response.json()

                setuserId(data.userData._id)
                setuserName(data.userData.username)
                setprofilePicture(data.userData.profilePicture)

                console.log("Fetched User Details")
                return data
            } else{
                const errorData = await response.json()
                setError("Failed To Fetch User Data")
                throw new Error(errorData.message)
            }
        }catch (error){
            console.error("Error Fetching User Data:", error)
            setError("Failed To Fetch User Data")
        }
    }
    fetchUserData();
}, [currentUser.uid]);

  async function handleLogOut(){
    setError('')

    try{
      await logout()
      navigate('/login')
    }catch{
      setError('Failed to log out')
    }
  }

  return (
    <div>
        <header className='garageHeader'>
            <h1 id="login_text">
                Setting
                <p id="slogan_text">Look At Your Details</p>
            </h1>

            <div className='userRow'>
              {profilePicture && (
                  <img className='showUserImage'
                      src={profilePicture} 
                      alt="Profile"
                  />
              )}
              <p className='showUserName'>{username}</p>
          </div>
        </header> 

        <p>{error}</p>
        <div className='postCard'>
          <div className='cardPostImage'>
              <img src={profilePicture} alt={username}/> 
          </div>

          <div className='cardContent'>
              <div className='cardHeader'>
                  <h2>Check Out My Profile</h2> 
                  <Link className='postVRN' to={`/race/${userId}`}></Link>
              </div>

              <div className='cardFooter'>
                  <p>Creator Of The Application</p>
              </div>

          </div>
        </div>

        <Link to="/reauthenticate" className="btn btn-primary w-100 mt-3"> Update Profile</Link>

        <Link to={`/garage/${userId}`} className="btn btn-dark w-100 mt-3"> Check Garage</Link>

        <div className="w-100 text-center mt-2">
            <Button variant="link" onClick={handleLogOut}> Log Out </Button>
        </div>

    </div>
  )
}

export default Setting
