import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import { useAuth } from './AuthContext'

import './SeePost_css.css'

const SeePost = () => {
    const {currentUser} = useAuth()

    const[posts, setPosts] = useState([])

    const[menuoptions, setMenuOptions] = useState(false)
    const[confirmDeletePost, setconfirmDeletePost] = useState(false)

    const[message, setMessage] = useState('')
    const[error, setError] = useState('')
    const[nopost, setNoPost] = useState('')
    const[loading, setLoading] = useState('')


    useEffect(() => {
        async function fetchPostData(){
            try{
                setError('')
                setMessage('')
                const response = await fetch(`http://localhost:3001/posts/view`, {
                    method: 'GET',
                    headers: {
                        'accept': 'application/json',
                    },
                });

                if (response.ok){
                    const data = await response.json()

                    const vehiclePost = await Promise.all(data.postData.map(async (post) => {
                        if(post.vehicles?.vehicleId){
                            const VehicleReponse = await fetch(`http://localhost:3001/vehicles/view?vehicleId=${encodeURIComponent(post.vehicles.vehicleId)}`, {
                                method: 'GET',
                                headers: {
                                    'accept': 'application/json',
                                },
                            });

                            if(VehicleReponse.ok){
                                const vehicle_Data = await VehicleReponse.json()
                                return {
                                    ...post,
                                     vehicle_Data
                                }
                            } else{
                                console.error("Error Fetching Vehicle Data:", error)
                                setError("Error Fetching Vehicle. Try Again Later")
                                return post                       
                            }
                        }
                        return post
                    }))

                    setPosts(vehiclePost)
                    console.log("Fetched Post Details")

                    if(!vehiclePost || vehiclePost.length === 0){
                        setNoPost("No car posts? Looks like the traffic jam is over. Be the first on the road!")
                    } 

                } else{
                    const errorData = await response.json()
                    throw new Error(errorData.message)
                }

            } catch (error){
                console.error("Error Fetching Post Data:", error)
                setError("Error Fetching Post. Try Again Later")
            }
        }
        fetchPostData();
    }, []);

    const formatDate = (timestamps) => {
        const date = new Date(timestamps);
        return date.toLocaleDateString()
    }
    const formatTime = (timestamps) => {
        const time = new Date(timestamps);
        return time.toLocaleTimeString()
    }

    const handleShowOptions = () =>{
        setMenuOptions(!menuoptions)
        setconfirmDeletePost(false)
    }

    const handleShowDelete = () => {
        setconfirmDeletePost(!confirmDeletePost)
    }

    async function handleDelete(postId) {
        try{
            setError('')
            setMessage('')
            setLoading(true)

            const response = await fetch(`http://localhost:3001/posts/delete?postId=${encodeURIComponent(postId)}`, {
                method: 'DELETE',
            });

            if (!response.ok){
                console.error("Error Deleting Post:")
            }
            const firebaseUID = currentUser.uid;

            const userDelete_response = await fetch(`http://localhost:3001/users/update?userfb=${encodeURIComponent(firebaseUID)}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({postIdRemove: postId})
            });

            if(!userDelete_response.ok){
                console.error("Error Deleting Post:")
            }
            console.log("Deleted Post")
            setMessage("Post Deleted")
            setTimeout(() => {
                window.location.reload(false);
            }, 2000)

        }catch (error){
            console.error("Error Deleting Post:")
            setError("Error Deleting Post. Try Again Later")
        }
        setLoading(false)
    }

    async function handleLike(postId) {
        try{
            setError('')
            setLoading(true)

            const response = await fetch(`http://localhost:3001/posts/update?postId=${encodeURIComponent(postId)}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({postIdRemove: postId})
            });

            if (!response.ok){
                console.error("Error Updating Like Post:")
            }

            console.log("Updated Like Post")
            setTimeout(() => {
                window.location.reload(false);
            }, 2000)

        }catch (error){
            console.error("Error Updating Like Post:")
            setError("Error Updating Post. Try Again Later")
        }
        setLoading(false)
    }

    return (
        <>
            <p className="w-100 text-center mt-2 mb-0" id="error_Msg">{nopost}</p>

            <div className="postList">
                {posts.map((post) => (
                    <div key={post._id}  className='post'>
                        <p className="w-100 text-center mt-2 mb-0" id="error_Msg">{error}</p>
                        <p className="w-100 text-center mt-2 mb-0" id="success_Msg">{message}</p>

                        <div className='postHead'>
                            <div className='postUserDetails'>
                                {post.user?.profilePicture && (
                                    <img className='postUserImage'
                                        src={`http://localhost:3001/${post.user.profilePicture}`} 
                                        alt="Profile"
                                    />
                                )}                                
                                <Link to={`/profile/${post.user._id}`} className='postUserName'>{post.user?.username}</Link>
                            </div>


                            <div className='postTimeStamp'>
                                <p>{formatDate(post.updatedAt)}</p>
                                <p>{formatTime(post.updatedAt)}</p>

                                <div className='postMenuContainer'>
                                    <button className='postMenu' onClick={handleShowOptions}>
                                        <div className='postdot'></div>
                                        <div className='postdot'></div>
                                        <div className='postdot'></div>
                                    </button>

                                    {menuoptions && (
                                        <div className='postOption'>
                                            <ul>
                                                <li className='menu-item'>
                                                    <Link to={`/editpost/${post._id}`} className='menu-link'>
                                                        Edit Post
                                                    </Link>
                                                </li>
                                                <li className='menu-item'>
                                                    <Link onClick={handleShowDelete} className='menu-link' id='delete-menu-link'>
                                                        Delete Post
                                                    </Link>
                                                </li>
                                            </ul>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {confirmDeletePost && (
                            <div>
                                <button className="deletepostbtn" disabled={loading} onClick={() => handleDelete(post._id)}>Confirm Delete</button>
                            </div>
                        )}

                        <div className='post-content'>
                            <h2 className='postTitle'>{post.title}</h2> 
                            <img src={`http://localhost:3001/${post.image}`} alt={post.title} className='postImage'/>

                            <div className='postSpecs'>
                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,red 0% ${((post.vehicle_Data?.vehicleData.vehicleInfo.Performance.MaxSpeed.Mph/350)*100)}%,white ${0}% 100%)`}}>
                                    <h3>{post.vehicle_Data?.vehicleData.vehicleInfo.Performance.MaxSpeed.Mph} 
                                        <span>mph</span>
                                    </h3>
                                    <p className='TopSpecsName'>Top Speed</p>
                                </div>

                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,orange 0% ${((post.vehicle_Data?.vehicleData.vehicleInfo.Performance.Power.Bhp/800)*100)}%,white ${0}% 100%)`}}>
                                    <h3>{post.vehicle_Data?.vehicleData.vehicleInfo.Performance.Power.Bhp}
                                        <span>bhp</span>
                                    </h3>
                                    <p className='TopSpecsName'>BHP</p>
                                </div>

                                <div className='TopSpecs' style={{background: `conic-gradient(from 0.5turn,green 0% ${((post.vehicle_Data?.vehicleData.vehicleInfo.Performance.Torque.Nm)/1000)*100}%,white ${0}% 100%)`}}>
                                    <h3>{post.vehicle_Data?.vehicleData.vehicleInfo.Performance.Torque.Nm}
                                        <span>Nm</span>
                                    </h3>
                                    <p className='TopSpecsName'>Torque</p>
                                </div>
                            </div>

                            <div className='postFooter'>
                                <p className='raceText'>Race Me</p>
                                <Link className='postVRN' to={`/race/${post.user._id}`}>{post.vehicles?.vrn}</Link>
                                <p className='postDescription'>{post.description}</p> 
                            </div> 
                        </div> 

                        <div className='postEngagement'>
                            <button className='likebtn' onClick={() => handleLike()}><svg xmlns="http://www.w3.org/2000/svg" width="1.7em" height="1.7em" viewBox="0 0 26 26"><path fill="currentColor" d="M17.869 3.889c-2.096 0-3.887 1.494-4.871 2.524c-.984-1.03-2.771-2.524-4.866-2.524C4.521 3.889 2 6.406 2 10.009c0 3.97 3.131 6.536 6.16 9.018c1.43 1.173 2.91 2.385 4.045 3.729c.191.225.471.355.765.355h.058c.295 0 .574-.131.764-.355c1.137-1.344 2.616-2.557 4.047-3.729C20.867 16.546 24 13.98 24 10.009c0-3.603-2.521-6.12-6.131-6.12"/></svg></button>
                            <button className='commentbtn'><svg xmlns="http://www.w3.org/2000/svg" width="1.7em" height="1.7em" viewBox="0 0 24 24"><path fill="currentColor" fill-rule="evenodd" d="M3 10.4c0-2.24 0-3.36.436-4.216a4 4 0 0 1 1.748-1.748C6.04 4 7.16 4 9.4 4h5.2c2.24 0 3.36 0 4.216.436a4 4 0 0 1 1.748 1.748C21 7.04 21 8.16 21 10.4v1.2c0 2.24 0 3.36-.436 4.216a4 4 0 0 1-1.748 1.748C17.96 18 16.84 18 14.6 18H7.414a1 1 0 0 0-.707.293l-2 2c-.63.63-1.707.184-1.707-.707V13zM9 8a1 1 0 0 0 0 2h6a1 1 0 1 0 0-2zm0 4a1 1 0 1 0 0 2h3a1 1 0 1 0 0-2z" clip-rule="evenodd"/></svg></button>
                            <button className='superfuel'><svg xmlns="http://www.w3.org/2000/svg" width="1.7em" height="1.7em" viewBox="0 0 16 16"><path fill="currentColor" d="M1 2a2 2 0 0 1 2-2h6a2 2 0 0 1 2 2v8a2 2 0 0 1 2 2v.5a.5.5 0 0 0 1 0V8h-.5a.5.5 0 0 1-.5-.5V4.375a.5.5 0 0 1 .5-.5h1.495c-.011-.476-.053-.894-.201-1.222a.97.97 0 0 0-.394-.458c-.184-.11-.464-.195-.9-.195a.5.5 0 0 1 0-1q.846-.002 1.412.336c.383.228.634.551.794.907c.295.655.294 1.465.294 2.081V7.5a.5.5 0 0 1-.5.5H15v4.5a1.5 1.5 0 0 1-3 0V12a1 1 0 0 0-1-1v4h.5a.5.5 0 0 1 0 1H.5a.5.5 0 0 1 0-1H1zm2.5 0a.5.5 0 0 0-.5.5v5a.5.5 0 0 0 .5.5h5a.5.5 0 0 0 .5-.5v-5a.5.5 0 0 0-.5-.5z"/></svg></button>
                        </div>

                    </div>
                ))}
            </div>    
        </>
    )
}

export default SeePost
