import React, {useRef, useState} from 'react'
import {Form, Button} from 'react-bootstrap'
import { useAuth } from './AuthContext'
import {Link, useNavigate} from 'react-router-dom'

const UpdateProfile = () => {

    const emailRef = useRef()
    const passwordRef = useRef()
    const passwordConfirmRef = useRef()
    const {currentUser, updateEmail, updatePassword} = useAuth()
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate()



    async function handleSubmit(e){
        e.preventDefault()

        if (passwordRef.current.value !== passwordConfirmRef.current.value){
            return setError("Password do not match")
        }
        if (passwordRef.current.value.length < 6 && passwordConfirmRef.current.value.length < 6 ){
            return setError("Password Too Short")
        }

        const toupdate = []
        setLoading(true)
        setError('')

        if (emailRef.current.value !== currentUser.email){
            toupdate.push(updateEmail(emailRef.current.value))
        }
        if (passwordRef.current.value !== currentUser.password){
            toupdate.push(updatePassword(passwordRef.current.value))
        }
        try{
            setError('')
            const {user} = await signup(emailRef.current.value, passwordRef.current.value)
            const firebaseUID = user.uid;

            const response = await fetch('http://localhost:3001/users/register?userfb=${encodeURIComponent(firebaseUID)}', {
                method: 'GET',
                headers: {
                    'accept': 'application/json',
                },
            });

            if (response.ok){
                const data = await response.json()
                console.log("Fetched User Details")
                navigate("/registervehicle")
                return data
            } else{
                const errorData = await response.json()
                if (errorData.message === 'Username Already Taken'){
                    return setError("Username Already in Use")
                }else{
                    throw new Error(errorData.message)
                }
            }
        }catch (error){
            console.error("Error Signing Up:", error)
            if (error.code === 'auth/email-already-in-use'){
                setError("Email Already In Use")
            } else if (error.code === 'auth/invalid-email'){
                setError("Invalid Email Address")
            }else{
                setError("Failed To Create Account")
            }
        }

        Promise.all(toupdate).then(() => {
            navigate('/')
        }).catch( () => {
            setError("Failed to Update Account")
        }).finally( () => {
            setLoading(false)
        })


        const vehicleRegValue = vehicleReg.current.value;

        fetch(`http://localhost:3001/vehicle/search?vrn=${encodeURIComponent(vehicleRegValue)}`)
    }


  return (
      <>
            <body>
                <h1 className="text-center mb-4">Update Profile</h1>
                <form onSubmit={handleSubmit}>

                    <Form.Group id="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" ref={emailRef}  defaultValue={currentUser.email} required/>
                    </Form.Group>            

                    <Form.Group id="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" ref={passwordRef} placeholder='Leave Blank to keep the same' />
                    </Form.Group>

                    <Form.Group id="passwordConfirmation">
                        <Form.Label>Password Confirmation</Form.Label>
                        <Form.Control type="password" ref={passwordConfirmRef} placeholder='Leave Blank to keep the same'  />
                    </Form.Group>

                    <p>{error}</p>

                    <Button disabled={loading} className="w-100 mt-2" type="submit">Update</Button>
                </form>

            </body>


        <div className="w-100 text-center mt-2">
            <Link to="/">  Go Back Home</Link>
        </div>
      </>

  )
}

export default UpdateProfile
