import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

// Fetch Firebase configuration and initialize Firebase app
async function fetchFirebaseConfig(){
    try {
        const response = await fetch('http://localhost:3001/firebaseConfig');

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.message);
        }

        const data = await response.json();

        // Initialize Firebase app with fetched configuration
        firebase.initializeApp({
            apiKey: data.apiKey,
            authDomain: data.authDomain,
            projectId: data.projectId,
            storageBucket: data.storageBucket,
            messagingSenderId: data.messagingSenderId,
            appId: data.appId
        });

        // Export Firebase Auth object directly
        return firebase.auth();
    } catch (error) {
        console.error("Error Initializing Firebase:", error);
        throw error;
    }
}

// Export the function to fetch Firebase Auth object
export const auth = fetchFirebaseConfig();
